'use client';

import React, { useRef, useState, useEffect } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { useTexture, Text, PerspectiveCamera } from '@react-three/drei';
import * as THREE from 'three';

// Individual feature configurations
const features = [
  {
    image:
      'data:image/jpeg;base64,',
    title: 'Amazing Feature 1',
    description: 'This feature will revolutionize your workflow.',
    transition: 'bottom',
    planePosition: [0, 3, 0],
    textPosition: [0, -4.5, 0],
  },
  {
    image:
      'data:image/jpeg;base64,',
    title: 'Incredible Feature 2',
    description: 'Boost your productivity with this game-changing feature.',
    transition: 'left',
    planePosition: [5, 0, 0],
    textPosition: [-7, 0, 0],
  },
  {
    image:
      'data:image/jpeg;base64,',
    title: 'Groundbreaking Feature 3',
    description: 'Experience the future of technology with this feature.',
    transition: 'zoom',
    planePosition: [0, 2, 0],
    textPosition: [0, 5, 0],
  },
  {
    image:
      'data:image/jpeg;base64,',
    title: 'Innovative Feature 4',
    description:
      'Transform your user experience with this cutting-edge feature.',
    transition: 'right',
    planePosition: [-5, 0, 0],
    textPosition: [7, 0, 0],
  },
  {
    image:
      'data:image/jpeg;base64,',
    title: 'Revolutionary Feature 5',
    description:
      'Stay ahead of the competition with this state-of-the-art feature.',
    transition: 'bottom',
    planePosition: [0, 3, 0],
    textPosition: [0, -4, 0],
  },
];

function AnimatedPlane({
  url,
  visible,
  transition,
  finalPosition,
  index,
  currentIndex,
}) {
  const texture = useTexture(url);
  const meshRef = useRef<THREE.Mesh>();

  useFrame(({ clock }) => {
    if (!meshRef.current) return;

    const totalTime = 7; // 7 seconds for each feature
    const transitionTime = 1.5; // 1.5 seconds for transition
    const t = visible
      ? Math.min((clock.getElapsedTime() % totalTime) / transitionTime, 1)
      : 0;
    let position = [0, 0, 0];
    let scale = visible ? 1 : 0;
    let rotation = [0, Math.sin(clock.getElapsedTime() * 0.5) * 0.1, 0];

    if (transition === 'bottom') {
      position = [0, THREE.MathUtils.lerp(-10, finalPosition[1], t), 0];
    } else if (transition === 'top') {
      position = [0, THREE.MathUtils.lerp(10, finalPosition[1], t), 0];
    } else if (transition === 'left') {
      position = [THREE.MathUtils.lerp(-10, finalPosition[0], t), 0, 0];
    } else if (transition === 'right') {
      position = [THREE.MathUtils.lerp(10, finalPosition[0], t), 0, 0];
    } else if (transition === 'zoom') {
      if (index === 2) {
        // Feature 3
        const zoomOutT = Math.max(0, (t - 0.5) * 2);
        position = [0, 0, THREE.MathUtils.lerp(-5, finalPosition[2], zoomOutT)];
        scale = THREE.MathUtils.lerp(3, 1, zoomOutT);
      } else if (index === 1) {
        // Feature 2
        const zoomInT = Math.min(t * 2, 1);
        position = [
          THREE.MathUtils.lerp(finalPosition[0], 3, zoomInT),
          THREE.MathUtils.lerp(finalPosition[1], 1, zoomInT),
          THREE.MathUtils.lerp(finalPosition[2], -0.5, zoomInT),
        ];
        scale = THREE.MathUtils.lerp(1, 3, zoomInT);
        rotation = [0, 0, 0];
      }
    }

    if (
      transition !== 'zoom' ||
      (transition === 'zoom' && index !== 1 && index !== 2)
    ) {
      scale = visible
        ? THREE.MathUtils.lerp(0, 1, t)
        : THREE.MathUtils.lerp(1, 0, t);
    }

    meshRef.current.position.lerp(new THREE.Vector3(...position), 0.1);
    meshRef.current.scale.lerp(new THREE.Vector3(scale, scale, scale), 0.1);
    meshRef.current.rotation.set(...rotation);
  });

  return (
    <mesh
      ref={meshRef}
      visible={
        visible || (transition === 'zoom' && index === 1 && currentIndex === 2)
      }
    >
      <planeGeometry args={[16 * 0.7, 9 * 0.7]} />
      <meshBasicMaterial
        map={texture}
        side={THREE.DoubleSide}
        transparent
        opacity={0.9}
      />
    </mesh>
  );
}

function AnimatedText({ text, position, visible, isTitle }) {
  const textRef = useRef<THREE.Mesh>();

  useFrame(({ camera, clock }) => {
    if (!textRef.current) return;
    textRef.current.quaternion.copy(camera.quaternion);
    if (visible) {
      const t = Math.min((clock.getElapsedTime() % 7) / 1.5, 1);
      const targetX = THREE.MathUtils.lerp(
        position[0] < 0 ? 20 : -20,
        position[0],
        t
      );
      const targetY = THREE.MathUtils.lerp(
        position[1] < 0 ? 20 : -20,
        position[1],
        t
      );
      textRef.current.position.lerp(
        new THREE.Vector3(targetX, targetY, position[2]),
        0.1
      );
    } else {
      textRef.current.position.lerp(
        new THREE.Vector3(
          position[0] < 0 ? 20 : -20,
          position[1] < 0 ? 20 : -20,
          position[2]
        ),
        0.1
      );
    }
  });

  return (
    <Text
      ref={textRef}
      position={position}
      fontSize={isTitle ? 0.8 : 0.4}
      color="#ffffff"
      anchorX="center"
      anchorY="middle"
      maxWidth={10}
    >
      {text}
    </Text>
  );
}

function Scene() {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % features.length);
    }, 7000);
    return () => clearInterval(interval);
  }, []);

  return (
    <>
      {features.map((feature, index) => (
        <React.Fragment key={index}>
          <AnimatedPlane
            url={feature.image}
            visible={index === currentIndex}
            transition={feature.transition}
            finalPosition={feature.planePosition}
            index={index}
            currentIndex={currentIndex}
          />
          <AnimatedText
            text={feature.title}
            position={[
              feature.textPosition[0],
              feature.textPosition[1],
              feature.textPosition[2],
            ]}
            visible={index === currentIndex}
            isTitle={true}
          />
          <AnimatedText
            text={feature.description}
            position={[
              feature.textPosition[0],
              feature.textPosition[1] - 1.5,
              feature.textPosition[2],
            ]}
            visible={index === currentIndex}
            isTitle={false}
          />
        </React.Fragment>
      ))}
    </>
  );
}

export default function Component() {
  return (
    <div className="w-full h-screen bg-black">
      <Canvas>
        <PerspectiveCamera makeDefault fov={75} position={[0, 0, 10]} />
        <color attach="background" args={['#000000']} />
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
        <Scene />
      </Canvas>
    </div>
  );
}
